userDefinedLinkHintCss:
div > .vimiumHintMarker {
  padding: 2px 3px;
  outline: none;
  box-shadow: none;
  background-color: #c0a36e;
  border: 0;
  border-radius: 2px;
  background-image: none;
}

div > .vimiumHintMarker span {
  font-size: 16px;
  font-weight: bold;
  text-shadow: none;
  color: #16161d;
}

div > .vimiumHintMarker > .matchingCharacter {
  color: #dcbc2a;
}
